mapper.update:
update admin_logs
set admin_logs_operate = #{adminLogsOperate,jdbcType=VARCHAR},
    admin_id = #{adminId,jdbcType=INTEGER},
    is_delete = #{isDelete,jdbcType=INTEGER},
    CREATED_TIME = #{createdTime,jdbcType=TIMESTAMP}
where admin_logs_id = #{adminLogsId,jdbcType=INTEGER}

mapper.select:
SELECT al.admin_logs_id as adminLogsId, al.admin_logs_operate as adminLogsOperate, a.admin_name as adminName, DATE_FORMAT(al.CREATED_TIME, '%Y-%m-%d %H:%i:%s') createdTime FROM admin_logs al left join admin a on al.admin_id = a.admin_id WHERE al.is_delete=0
and al.admin_id = #{adminId,jdbcType=INTEGER}
order by al.CREATED_TIME desc
