mapper.update:
update auction_schedule
set goods_id = #{goodsId,jdbcType=INTEGER},
  user_id = #{userId,jdbcType=INTEGER},
  auction_schedule_price = #{auctionSchedulePrice,jdbcType=DECIMAL},
  CREATED_TIME = #{createdTime,jdbcType=TIMESTAMP}
where auction_schedule_id = #{auctionScheduleId,jdbcType=INTEGER}

mapper.select:
SELECT a.auction_schedule_id as auctionScheduleId, a.goods_id as goodsId, g.goods_name as goodsName, a.user_id as userId, u.user_name as userName, a.auction_schedule_price as auctionSchedulePrice, DATE_FORMAT(a.CREATED_TIME, '%Y-%m-%d %H:%i:%s') createdTime FROM auction_schedule a left join goods g on a.goods_id = g.goods_id left join user u on a.user_id = u.user_id WHERE 1=1
and a.goods_id=#{goodsId}
order by a.CREATED_TIME desc
