mapper.select:
SELECT m.user_id as userId, m.openid as openid, m.id_card as idCard, m.user_number as userNumber, m.user_pwd as userPwd, m.user_name as userName, m.user_gender as userGender, m.user_phone as userPhone, m.user_address as userAddress, m.user_credit as userCredit, m.is_delete as isDelete, DATE_FORMAT(m.CREATED_TIME, '%Y-%m-%d %H:%i:%s') createdTime, m.CREATED_BY as createdBy FROM user m where m.is_delete=0
and m.openid=#{openid}
and CONCAT(m.user_name) like concat('%',#{keyword},'%')
